import React from "react";
import SummaryOwnContent from './SummaryOwnContent'
import ProfileImg1 from '../../../profile1.jpg'
import ProfileImg2 from '../../../profile3.jpeg'

function SummaryYouOwn(props){
    var arr= [];
    props.userdata.map(item=>{
        if(arr.indexOf(item.name) === -1){
            arr.push(item.name)
        }    
    })
    const summaryOwn =[];
    for(let i in arr){
        var img = pr
        const filter = props.userdata.filter(item=>{
            if(item.name === arr[i]){
                return <SummaryOwnContent key={item.id} aClass="sumary-you-own-inside-p" url={ProfileImg2} amount={(item.amount)/2} name={item.name}/>
            }
        })
        if(filter.length >1){
            var amount=0;
            for(let j in filter){
                amount+=filter[j].amount;
            }
            summaryOwn.push(<SummaryOwnContent key={filter[0].id} aClass="sumary-you-own-inside-p" url={ProfileImg2} amount={(amount)/2} name={filter[0].name}/>)
        }
        else{
            summaryOwn.push(<SummaryOwnContent key={filter[0].id} aClass="sumary-you-own-inside-p" url={ProfileImg2} amount={(filter[0].amount)/2} name={filter[0].name}/>)
        }
    }

    return (
        <div className="you-are-owe">
            {summaryOwn}
        </div>
    )
}

export default SummaryYouOwn
