import React from 'react'
import SummaryOwnContent from './SummaryOwnContent'
import ProfileImg1 from '../../../../Components/profile1.jpg'

function SummaryOwn(props){
    const summaryYouOwn = props.userdata.map(item=>{
        return <SummaryOwnContent aClass="sumary-own-inside-p" url={ProfileImg1} amount={(item.amount)/2} name={item.name}/>
    })
    var arr= [];
    props.userdata.map(item=>{
        if(arr.indexOf(item.name) === -1){
            arr.push(item.name)
        }    
    })
    const summaryOwn =[];
    for(let i in arr){
        const filter = props.userdata.filter(item=>{
            if(item.name === arr[i]){
                return <SummaryOwnContent key={item.id} aClass="sumary-you-own-inside-p" url={ProfileImg3} amount={(item.amount)/2} name={item.name}/>
            }
        })
        if(filter.length >1){
            var amount=0;
            for(let j in filter){
                amount+=filter[j].amount;
            }
            summaryOwn.push(<SummaryOwnContent key={filter[0].id} aClass="sumary-you-own-inside-p" url={ProfileImg3} amount={(amount)/2} name={filter[0].name}/>)
        }
        else{
            summaryOwn.push(<SummaryOwnContent key={filter[0].id} aClass="sumary-you-own-inside-p" url={ProfileImg3} amount={(filter[0].amount)/2} name={filter[0].name}/>)
        }
    }
    return(
        <div className="you-owe">
            {summaryYouOwn}
        </div>
    )
}

export default SummaryOwn
