import React,{useState,useCallback} from 'react'
import Button from '../Button'
import ProfileImg1 from '../profile1.jpg'
import Arrow from './settle-up-arrow.png'
import ProfileImg2 from '../profile2.jpeg'

function SettleExpense(props){
    const [settle,setSettle] = useState({
        name:"",
        paymentId:""
    })
    function handleSettleVisibility(){
        const currentViewModal = props.viewModal.map((item) =>{
            item.settle = false
            return item
        })
        props.method(currentViewModal)
    }
    function handleSettleDashVisibility(){
        const currentViewModal = props.viewModal.map((item) =>{
            if(item.visibility === true){
                item.visibility = !item.visibility
            }
            if(item.dash === "dash-outro"){
                item.visibility = !item.visibility
            }
            item.settle = false
            return item
        })
        props.method(currentViewModal)
    }
    function resizeInput(event) {
        event.target.style.width =  event.target.value.length + "ch";
    }
    const [payment,setPayment] = useState([])
    var payments = []
    function handleUserName(event){
        // alert(event.target.value)
        setSettle(prevSettle=>{
            prevSettle.name = event.target.value;
            return prevSettle
        })
        props.expenses.map(item=>{
            if(settle.name === item.user_name){
                payments.push(item.id+": "+item.description+": "+item.user_name+": "+item.amount)
            }
        })
        setPayment(prev=>{
            prev = payments;
            return prev;
        })
    }
    const option = useCallback(
        () => {
            return(
                payment.map(item=>{
                    return <option key={item.substr(0,2)} value={item}>{item}</option>
                })
            )
        },[payment])
    var names = []
    var arr= [];
    var arr2 = [];
    const [useNames,setUseNames] = useState([])
    props.expenses.map(item=>{
        if( props.user[1] !== item.user_name && arr.indexOf(item.user_name) === -1){
            arr.push(item.user_name)
            arr2.push({name:item.user_name,id:item.id})
        }    
        return arr
    }) 
    arr.map(item=>{
        for(let i in arr2){
            if(item === arr2[i].name){
                names.push(item)
            }
        }
    })
    setUseNames(prev=>{
        prev= names
        return prev
    })
    const mainOption = useCallback(
        () => {
            return(
                useNames.map(item=>{
                    return <option key={item.substr(0,2)} value={item}>{item}</option>
                })
            )
        },[useNames])
    return(
        <div style={{display:props.viewModal[4].settle?"block":"none"}} className="modal-overlay">
            <div className="add-expense-modal">
                <div className="add-expense-1">
                    <p>Settle up</p>
                    <Button class="close-btn" name="x" onclickmeth={handleSettleVisibility}/>
                </div>
                <div className="add-expense-2">
                    <div className="add-expense-5">
                        <div style={{display:"flex",marginLeft:"55px"}}>
                            <img className="settle-img" src={ProfileImg1} />
                            <img className="settle-img-arrow" src={Arrow} />
                            <img className="settle-img" src={ProfileImg2} />
                        </div>
                        <br/>
                        <div style={{display:"flex",marginLeft:"60px"}}>
                            <p style={{width:"30px"}} className="option-list-settle">You</p>
                            <p style={{marginRight:"5px",marginLeft:"5px",marginTop:"3px"}}>paid</p>
                            <select onChange={handleUserName} className="option-list-settle">
                                <option>Select your friend</option>
                                {mainOption()}
                            </select>
                        </div>
                        <br/>
                        <select onChange="" className="option-list-settle">
                                {option()}
                            </select>
                        <br/>
                        <div className="add-expense-5-inside">
                            <input className="input-6" type="number" placeholder="0.00" onChange={resizeInput}/><span className="dollar-sym-2">$</span>
                        </div>
                    </div>
                    <div style={{padding:"10px",textAlign:"right"}}>
                        <Button class="exp-btn" name="Cancel" onclickmeth={handleSettleVisibility}/>
                        <Button class="exp-btn-2" name="Save" onclickmeth={handleSettleDashVisibility} />
                    </div>

                </div> 
            </div>
        </div>
    )
}

export default SettleExpense
