import React from 'react'
import ManImg from '../../man.png'
import SummaryChartYouOwn from './SummaryChartYouOwn'

function SummaryChart(props){
    const summaryChartOwn = props.userData.map(item=>{
        return <SummaryChartYouOwn aClass="chart-you-are-owe-inside"  amount={(item.amount)/2} name={item.name}/>
    })
    // const summaryChartYouOwn = props.userDataYou.map(items=>{
    //     return <SummaryChartYouOwn aClass="chart-you-owe-inside"  amount={(items.amount)/2} name={items.name}/>
    // })
    var arr= [];
    props.userDataYou.map(item=>{
        if(arr.indexOf(item.name) === -1){
            arr.push(item.name)
        }    
    })
    const summaryChartYouOwn =[];
    for(let i in arr){
        const filter = props.userDataYou.filter(item=>{
            if(item.name === arr[i]){
                return <SummaryChartYouOwn key={item.id} aClass="chart-you-owe-inside"  amount={(item.amount)/2} name={item.name}/>
            }
        })
        if(filter.length >1){
            var amount=0;
            for(let j in filter){
                amount+=filter[j].amount;
            }

            summaryChartYouOwn.push(<SummaryChartYouOwn key={filter[0].id} aClass="chart-you-owe-inside"  amount={(amount)/2} name={filter[0].name}/>)
        }
        else{
            summaryChartYouOwn.push(<SummaryChartYouOwn key={filter[0].id} aClass="chart-you-owe-inside"  amount={(filter[0].amount)/2} name={filter[0].name}/>)
        }
    }
    return(
        <div style={{display:props.show.chart?"flex":"none"}} className="summary-chart">
            <div className="chart-you-owe">
                {summaryChartYouOwn}
            </div>
            <div className="image-wrapper">
                <img className="man-img-charts" src={ManImg} alt="man standing alone"/>
            </div>
            <div className="chart-you-owe">
                {summaryChartOwn}
            </div>
        </div>
    )
}

export default SummaryChart
