import React from 'react'

function CollectionContainer(props){
    var description;
    if(props.description.length == 0){
        description = <li className="li-collection-desc">You do not have any {props.tag} yet.</li>
    }
    else{
        var arr= [];
        var arr2 = [];
        props.description.map(item=>{
            if(arr.indexOf(item.name) === -1){
                arr.push(item.name)
                arr2.push({name:item.name,id:item.id})
            }    
        }) 
        description = arr.map(item=>{
            for(let i in arr2){
                if(item === arr2[i].name){
                    return <li className="li-collection" key={arr2[i].id} onClick={()=>{alert(arr2[i].id)}}><i className="fa fa-user"></i> {item}</li>
                }
            }
        })
    }

    return (
        <div className="collection">
            <div className="collection-top-wrap">
                <p>{props.name}</p>
                <div className="collection-margin"><a className="collection-a" href="">+ add</a></div>
            </div>
            <div className="collection-list">
                <ul className="ul-collection">
                    {description}
                </ul>
            </div>
        </div>
    );
}

export default CollectionContainer
