import React from 'react'
import SummaryOwnContent from './SummaryOwnContent'
import ProfileImg1 from '../../../../Components/profile1.jpg'

function SummaryOwn(props){

    var arr= [];
    props.userdata.map(item=>{
        if(props.currentUser === item.owe_name && arr.indexOf(item.user_name) === -1){
            arr.push(item.user_name)
        }    
    })
    const summaryYouOwn =[];
    for(let i in arr){
        const filter = props.userdata.filter(item=>{
            if(item.user_name === arr[i]){
                return item
            }
        })
        if(filter.length >1){
            var amount=0;
            for(let j in filter){
                amount+=parseFloat(filter[j].amount);
            }
            summaryYouOwn.push(<SummaryOwnContent onclick={()=>{props.method(filter[0].user_name);props.method3(filter[0].user_name);props.method2()}} key={filter[0].user_name} aClass="sumary-own-inside-p" url={ProfileImg1} amount={(amount)/2} name={filter[0].user_name}/>)
        }
        else{
            summaryYouOwn.push(<SummaryOwnContent onclick={()=>{props.method(filter[0].user_name);props.method3(filter[0].user_name);props.method2()}} key={filter[0].user_name} aClass="sumary-own-inside-p" url={ProfileImg1} amount={parseFloat(filter[0].amount)/2-filter[0].amountPaid} name={filter[0].user_name}/>)
        }
    }
    return(
        <div className="you-owe">
            {summaryYouOwn.reverse()}
        </div>
    )
}

export default SummaryOwn
