import React from 'react'
import Button from '../Button'

function DashboardHeader(props){
    function handleChangeExp(){
        const currentView = props.viewType.map(item =>{
            item.modal = true
            return item
        })
        props.method(currentView)
    }
    function handleChangeOut(){
        const currentView = props.viewType.map(item =>{
            item.settle = true
            return item
        })
        props.method(currentView)
    }
    function RenderHeader(props){
        const header = props.view.map(item => {
            if (item.visibility) {
                if(item.dash == "dash-exp"){
                    return 
                }
                if(item.dash == "recent-activity"){
                    return <h3 className="h3-dashboard" >Recent Activity</h3>
                }
                if(item.dash == "all-expenses"){
                    return <h3 className="h3-dashboard" >All Expenses</h3>
                }
            }
        })
        return header
    }

    return (
        <div className="dashboard-header">
            <h3 className="h3-dashboard" >Dashboard</h3>
            <div className="btn-margin">
                <Button class="dashboard-btn-left" name="Add an expense" onclickmeth={handleChangeExp} />
                <Button class="dashboard-btn-right" name="Settle up" onclickmeth={handleChangeOut}/>
            </div>
        </div>
    )
}

export default DashboardHeader
