import React from "react";
import SummaryOwnContent from './SummaryOwnContent'
import ProfileImg1 from '../../../profile1.jpg'
import ProfileImg2 from '../../../profile3.jpeg'

function SummaryYouOwn(props){
    var arr= [];
    props.userdata.map(item=>{
        if(props.currentUser === item.user_name && arr.indexOf(item.user_name) === -1){
            arr.push(item.owe_name)
        }    
    })
    console.log(arr)
    const summaryYouOwn =[];
    for(let i in arr){
        const filter = []
        props.userdata.filter(item=>{
            if(item.owe_name === arr[i]){
                filter.push(item)
            }
        })
        // console.log(filter)
        if(filter.length >1){
            var amount=0;
            for(let j in filter){
                amount+=parseFloat(filter[j].amount);
            }
            summaryYouOwn.push(<SummaryOwnContent key={filter[0].id} aClass="sumary-you-own-inside-p" url={ProfileImg1} amount={(amount)/2} name={filter[0].owe_name}/>)
        }
        else{
            summaryYouOwn.push(<SummaryOwnContent key={filter[0].id} aClass="sumary-you-own-inside-p" url={ProfileImg1} amount={parseFloat(filter[0].amount)/2} name={filter[0].owe_name}/>)
        }
    }
    return (
        <div className="you-are-owe">
            {summaryYouOwn}
        </div>
    )
}

export default SummaryYouOwn
