import React from 'react'
import SummaryOwnContent from './SummaryOwnContent'
import ProfileImg1 from '../../../../Components/profile1.jpg'

function SummaryOwn(props){

    var arr= [];
    props.userdata.map(item=>{
        if(props.currentUser === item.owe_name && arr.indexOf(item.owe_name) === -1){
            arr.push(item.user_name)
        }    
    })
    // console.log(arr)
    const summaryYouOwn =[];
    for(let i in arr){
        const filter = props.userdata.filter(item=>{
            if(item.user_name === arr[i]){
                return item
            }
        })
        if(filter.length >1){
            console.log(filter)
            var amount=0;
            for(let j in filter){
                amount+=parseFloat(filter[j].amount);
            }
            summaryYouOwn.push(<SummaryOwnContent key={filter[0].id} aClass="sumary-own-inside-p" url={ProfileImg1} amount={(amount)/2} name={filter[0].owe_name}/>)
        }
        else{
            summaryYouOwn.push(<SummaryOwnContent key={filter.id} aClass="sumary-own-inside-p" url={ProfileImg1} amount={parseFloat(filter.amount)/2} name={filter.owe_name}/>)
        }
    }
    return(
        <div className="you-owe">
            {summaryYouOwn}
        </div>
    )
}

export default SummaryOwn
