import React from 'react'
import ManImg from '../../man.png'
import SummaryChartYouOwn from './SummaryChartYouOwn'

function SummaryChart(props){
    var arr= [];
    props.userData.map(item=>{
        if(props.currentUser === item.user_name && arr.indexOf(item.owe_name) === -1){
            arr.push(item.owe_name)
        }    
    })
    const summaryChartYouOwn =[];
    for(let i in arr){
        const filter = props.userData.filter(item=>{
            if(item.name === arr[i]){
                return <SummaryChartYouOwn key={item.id} aClass="chart-you-are-owe-inside"  amount={(item.amount)/2} name={item.name}/>
            }
        })
        if(filter.length >1){
            var amount=0;
            for(let j in filter){
                amount+=parseFloat(filter[j].amount);
            }

            summaryChartYouOwn.push(<SummaryChartYouOwn key={filter[0].id} aClass="chart-you-are-owe-inside"  amount={(amount)/2} name={filter[0].name}/>)
        }
        else{
            summaryChartYouOwn.push(<SummaryChartYouOwn key={filter[0].id} aClass="chart-you-are-owe-inside"  amount={parseFloat(filter[0].amount)/2} name={filter[0].name}/>)
        }
