    //             amount+=parseFloat(filter[j].amount);
    //         }

    //         summaryChartYouOwn.push(<SummaryChartYouOwn key={filter[0].id} aClass="chart-you-owe-inside"  amount={(amount)/2} name={filter[0].name}/>)
