import React, { useEffect,useState } from 'react';
import AllExpenseDetails from '../AllExpense/AllExpenseDetails'

function Friends(props){
    const expensesOwe = []
    useEffect(() => {
        const loading = true;
        if(loading){
            expensesOwe = <p>loading...</p>
        }
        return setTimeout(() => {
            loading=false
        },2000);
    }, [])
    expensesOwe= props.expenses.map(item=>{
        var month = item.created_at.substr(4,4);
        var date = item.created_at.substr(7,4);
        if(item.user_name === props.name || item.owe_name === props.name){
            if(props.userData === item.user_name)
                return <AllExpenseDetails description={item.description} lent="you lent " paid="you paid " name={item.owe_name} amount={item.amount} monthOf={month} dateOf={date}/>
            if(props.userData === item.owe_name)
                return <AllExpenseDetails description={item.description} lent="lent you " paid=" paid " name={item.user_name} amount={item.amount} monthOf={month} dateOf={date}/>
        }
    })
    return(
        <div>
            {expensesOwe}
        </div>
    )
}

export default Friends;
